import {useState} from 'react';


const Register = () => {

    const [getData,setData] = useState({
        userName:'',
        email:'',
        password:''
    })

    const onChangeHandler=(event)=>{
         setData({...getData,[event.target.name]:event.target.value});
    }

    const onSubmitHandler=()=>{
         if(!getData.userName || !getData.email || !password){
              alert("please provide the details");
              return;
         }
           console.log(getData);

    }

    return (<div className="container">
        <div className="col-md-4"></div>
        <div className="col-md-4">
            <form>
                <div class="form-group">
                    <label for="userName">UserName</label>
                    <input type="text" class="form-control" onChange={onChangeHandler} id="userName" name="userName" placeholder="Enter userName" />
                </div>
                <div class="form-group">
                    <label for="email">Email address</label>
                    <input type="email" class="form-control" onChange={onChangeHandler} id="email" name="email" placeholder="Enter email" />
                </div>
                <div class="form-group">
                    <label for="password">Password</label>
                    <input type="password" class="form-control" onChange={onChangeHandler} id="password" name="password" placeholder="Password" />
                </div>
                <button onClick={onSubmitHandler} type="submit" class="btn btn-primary">Submit</button>
            </form>
        </div>
        <div className="col-md-4"></div>
    </div>)
}
export default Register;
